var paths = require('dewm-paths');
var templates = {danville:{},stacks:{},goat:{header:{section:{}},section:{},menu:{}},talk:{opener:{}},toc:{},elements:{cartoon:{}}};

templates.issue = function(dates,i) {
	return {
		"date":dates.dates[i], 
		"string":dates.strings[i], 
		"hyphen":dates.stringsWithHyphens[i], 
		"formatted":dates.stringsFormatted[i]
	};
};

templates.week = function(dates,i) {
	return {
		"files":[],
		"stacks":[],
		"init":false,
		"modified":new Date(),
		"sidecar":paths.sidecar.replace(/<insert_hyphen_date>/g,dates.stringsWithHyphens[i]),
		"orange":paths.orange.replace(/<insert_hyphen_date>/g,dates.stringsWithHyphens[i]),
		"green":paths.green.replace(/<insert_date>/g,dates.strings[i]),
		"condenet":paths.condenet.replace(/<insert_hyphen_date>/g,dates.stringsWithHyphens[i])
	};
};

templates.stack = function(title,stacks,stackDate,i) {
	return { 
		"title":title.split(" by ")[0].replace("”","").replace("“",""), 
		"stack":stacks[i].folderName[0], 
		"issue":stackDate, 
		"author":title.split(" by ")[1], 
		"kicker":stacks[i].kicker[0], 
		"description":stacks[i].description[0],
		"danville":null,
		"tablet":null,
		"xmlfilename":stacks[i].xmlfilename[0]
	};
};

templates.assignment = function(stack) {
	return { 
		"stack":stack,
		"copy":{ "readers":[],"total":0,"approved":0 },
		"makeup":{ "built":false,"copy":false,"makeup":false },
		"art":{ "username":null,"status":[false,false,false,false,false,false,false],"app":false,"layout":null }		
	};
};

